note_models.css:
.card {
  font-family: arial;
  font-size: 20px;
  color: white;
  background-color: #1e272e
}

#feedback, #submit{
		font-size: 20px;
	}

    #submit{
    	background: #f53b57;
        color: white;
        border: 0;
        padding: 10px 20px 10px 20px;
        border-radius: 5px;
				 outline: none;
    }
    #submit:hover{
    	cursor: pointer;
      background: #e52b47;
    }

        #submit:active{
    	cursor: pointer;
        background: #c50b27;
    }

    #feedbackwrapper{
    	   display: none;
        align-items: center;
        flex-direction: column;
    }


		/* LOADER STYLES */
.loader {
  border: 16px solid #f3f3f3; /* Light grey */
  border-top: 16px solid #3498db; /* Blue */
  border-radius: 50%;
  width: 120px;
  height: 120px;
  animation: spin 2s linear infinite;
	opacity: 0.8;
	position: absolute;
	display: none;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

